const JSBI = require("jsbi");
const { bi, lessThan, add } = require("jsbi-utils");
const { bufferToHex, ripemd160 } = require("ethereumjs-util");
const ethers = require("ethers");
const LeapProvider = require("leap-provider");
const { Tx, helpers, Input, Output } = require("leap-core");

const erc20ABI = require("../src/contracts/StableCoin.abi");
const earth = require("../src/planeta/contracts/Earth");

const { LeapEthers } = helpers;

const CONFIG = {
  //dryRun: JSON.parse(process.env.DRY_RUN) || false, //Tells you what it would do without actually sending any txs
  provider: process.env.NODE_URL || "https://testnet-node.leapdao.org",
  color: process.env.COLOR || 0,
  utxoNum: parseInt(process.env.UTXO_NUM) || 20, //Number of UTXOs to keep
  priv: "0x6d57790cf6ce277bcedd95d69ebc1745eb990ee20e1f2d9a7ef2008124b72c53"
};
//earth.code = '';

const plasma = new LeapEthers(new LeapProvider(CONFIG.provider));

const contractAddress = bufferToHex(ripemd160(earth.code));

const consolidate = async () => {
  console.log("Contract address:", contractAddress);
  const utxos = await plasma.getUnspent(contractAddress, CONFIG.color);
  console.log("Number of UTXOs:", utxos.length);

  // TODO pick the 10 smallest ones

  const GAS_COST = bi(10);

  const colors = await plasma.getColors();
  const gasTokenAddress = colors[0];
  const gasToken = new ethers.Contract(gasTokenAddress, erc20ABI, plasma);
  const gasBalance = bi(await gasToken.balanceOf(contractAddress));
  if (lessThan(gasBalance, GAS_COST)) {
    throw new Error(
      `Not enough gas for consolidationg. Need: ${GAS_COST}, balance: ${gasBalance}`
    );
  }
  const gasUtxos = await plasma.getUnspent(contractAddress, 0);
  const gasUtxo = gasUtxos[0];

  let inputs = [];
  const gasInput = new Input({
    prevout: gasUtxo.outpoint,
    script: earth.code
  });
  inputs.push(gasInput);

  const tokenAddr = colors[CONFIG.color];

  for (let i = 0; i < 1; i++) {
    console.log(utxos[i]);
    inputs.push(
      new Input({
        prevout: utxos[i].outpoint
      })
    );
  }

  console.log(inputs);

  let condition = Tx.spendCond(inputs);
  const { v, r, s } = condition.getConditionSig(CONFIG.priv);
  const earthContract = new ethers.Contract(contractAddress, earth.abi, plasma);
  console.log(tokenAddr);
  const msgData = earthContract.interface.functions.consolidate(tokenAddr, v, r, s).data;
  console.log(msgData);

  condition.inputs[0].setMsgData(msgData);
  
  const outputs = await plasma.provider
    .send("checkSpendingCondition", [condition.hex()])
    .then(rsp => {
      console.log("checkSpendingCondition", rsp);
      return rsp.result.outputs;
    })
    .catch(err => {
      console.log("checkSpendingCondition", err);
      return err.logOuts;
    });

    console.log(outputs);
  condition.inputs[0].setMsgData(msgData);
  condition.outputs = outputs;

  const rsp = await plasma.provider
    .send("checkSpendingCondition", [condition.hex()]);

  console.log(rsp);
  if (dryRun) return rsp.error ? rsp.error : "Check passed"; //Do not send transaction in dry run mode
  if (rsp.error) console.log(JSON.stringify(rsp.error));

  //const txHash = await wallet.provider.send('eth_sendRawTransaction', [condition.hex()]);
  console.log("txHash:", txHash);

  return txHash;
};

consolidate();
